INSERT INTO `wp_posts` VALUES (14,1,'2024-07-18 11:19:39','2024-07-18 11:19:39','<!-- wp:heading {\"textAlign\":\"center\"} -->\n<h2 class=\"wp-block-heading has-text-align-center\">Conoce nuestros servicios que impulsarán tu negocio:</h2>\n<!-- /wp:heading -->\n\n<!-- wp:group {\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\"><!-- wp:group {\"layout\":{\"type\":\"flex\",\"flexWrap\":\"nowrap\"}} -->\n<div class=\"wp-block-group\"><!-- wp:group {\"layout\":{\"type\":\"flex\",\"orientation\":\"vertical\",\"justifyContent\":\"center\"}} -->\n<div class=\"wp-block-group\"><!-- wp:image {\"id\":26,\"width\":\"319px\",\"height\":\"auto\",\"aspectRatio\":\"1\",\"scale\":\"cover\",\"sizeSlug\":\"medium\",\"linkDestination\":\"none\",\"className\":\"is-style-rounded\"} -->\n<figure class=\"wp-block-image size-medium is-resized is-style-rounded\"><img src=\"http://localhost:10013/wp-content/uploads/2024/07/Kit-digital_MKAE-300x169.jpg\" alt=\"\" class=\"wp-image-26\" style=\"aspect-ratio:1;object-fit:cover;width:319px;height:auto\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"level\":3,\"style\":{\"elements\":{\"link\":{\"color\":{\"text\":\"var:preset|color|ast-global-color-8\"}}}},\"backgroundColor\":\"pale-pink\",\"textColor\":\"ast-global-color-8\"} -->\n<h3 class=\"wp-block-heading has-text-align-center has-ast-global-color-8-color has-pale-pink-background-color has-text-color has-background has-link-color\">Diseño</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">Los diseños más atractivos que reflejen la personalidad y valores suyos o de su empresa</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"layout\":{\"type\":\"flex\",\"orientation\":\"vertical\",\"justifyContent\":\"center\"}} -->\n<div class=\"wp-block-group\"><!-- wp:image {\"id\":22,\"width\":\"319px\",\"height\":\"auto\",\"aspectRatio\":\"1\",\"scale\":\"cover\",\"s